import React, { useEffect, useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import './Login.css';

export default function Login() {
    //Una vez logueado, si el usuario intenta volver a la pagina de login, se redirecciona a la pagina principal
    useEffect(() => {
        if (localStorage.getItem('token')) {
            alert('Atención ya estás logueado, redirigiendo al home');
            window.location.href = '/';
        }
    }, []);

    const [values, setValues] = useState();

    const formik = useFormik({
        initialValues: {
            email: '',
            password: ''
        },
        validationSchema: Yup.object({
            email: Yup.string()
                .email('Email inválido')
                .required('El campo es requerido'),
            password: Yup.string().required('El campo es requerido')
        }),
        onSubmit: (values) => {
            setValues(values);
        }
    });
    // console.log(values);

    //Enviar datos al servidor para validar usuario, si es correcto, se guarda el token en el localStorage y se redirecciona a la pagina principal
    useEffect(() => {
        const getToken = async () => {
            const response = await axios.post(
                'http://challenge-react.alkemy.org/',
                values
            );
            console.log(response.data.token);
            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
                document.cookie =
                    'isLoggedIn=' +
                    encodeURIComponent(true) +
                    ';' +
                    'expires=0';
                window.location.href = '/';
            }
        };
        getToken();
    }, [values]);

    // console.log(document.cookie.isLoggedIn);

    return (
        <>
            <form onSubmit={formik.handleSubmit}>
                <fieldset>
                    <div>
                        <label>Email</label>
                        <input
                            type="email"
                            name="email"
                            placeholder="Ingresa tu email"
                            onChange={formik.handleChange}
                            value={formik.values.email}
                        />
                        {formik.errors.email ? (
                            <div className="form-login__error">
                                {formik.errors.email}
                            </div>
                        ) : null}
                    </div>
                    <div>
                        <label>Contraseña</label>
                        <input
                            type="password"
                            name="password"
                            placeholder="Ingresa tu contraseña"
                            onChange={formik.handleChange}
                            value={formik.values.password}
                        />
                        {formik.errors.password ? (
                            <div className="form-login__error">
                                {formik.errors.password}
                            </div>
                        ) : null}
                    </div>
                    <div>
                        <input type="submit" value="Enviar"></input>
                    </div>
                </fieldset>
            </form>
        </>
    );
}
