import React from 'react';
import HeroCardStats from './HeroCardStats';

export default function CardHeroes(props) {
    console.log(props.heroe);
    let image = props.heroe.image.url,
        name = props.heroe.name;
    return (
        <div
            className="container"
            style={{
                marginTop: '2rem'
            }}
        >
            <div className="card">
                <div className="card-body">
                    <img
                        className="card-img-top"
                        src={image}
                        alt={name}
                    />
                    <h5 className="card-title text-center">{name}</h5>
                    {props.heroe.powerstats.map((stats) => {
                        return <HeroCardStats stats={stats} />;
                    })}
                    <div className="container">
                        <div
                            className="row"
                            style={{
                                justifyContent: 'center'
                            }}
                        >
                            <button className="btn btn-link w-75">
                                ver más
                            </button>
                            <button className="btn btn-dark w-75">
                                Eliminar
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
