import React from 'react';

export default function HeroCardShowMoreInfo(props) {
    const { appearance, biography, work } = props;

    let weight = appearance.weight[1],
        height = appearance.height[1],
        name = biography['full-name'],
        eyeColor = appearance['eye-color'],
        hairColor = appearance['hair-color'],
        workPlace = work.base;

    console.log(work);

    return (
        <>
            <div
                className="card-header text-center"
                style={{
                    fontSize: '1.5rem',
                    fontWeight: 'bold'
                }}
            >
                Caracteristicas
            </div>
            <ul className="list-group list-group-flush">
                <li className="list-group-item">Peso: {weight}</li>
                <li className="list-group-item">Altura: {height}</li>
                <li className="list-group-item">Nombre: {name}</li>
                <li className="list-group-item"></li>
                <li className="list-group-item">
                    Color de ojos: {eyeColor}
                </li>
                <li className="list-group-item">
                    Color de cabello:{hairColor}
                </li>
                <li className="list-group-item">
                    Lugar de trabajo: {workPlace}
                </li>
            </ul>
        </>
    );
}
