import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import HeroCard from './HeroCard';

export default function SearchHero() {
    //Manejo del formulario
    const [valuesForm, setValuesForm] = useState();
    const [heroes, setHeroes] = useState([]);

    const formik = useFormik({
        initialValues: {
            hero: ''
        },
        validationSchema: Yup.object({
            hero: Yup.string().required(
                'Por favor ingrese un nombre válido para continuar'
            )
        }),

        onSubmit: (values) => {
            setValuesForm(values);
        }
    });

    //Petición busqueda de heroes
    useEffect(() => {
        const getHeroe = async () => {
            try {
                const response = await axios.get(
                    `https://www.superheroapi.com/api.php/10221283496530128/search/${valuesForm.hero}`
                );
                setHeroes(response.data.results);
            } catch (error) {
                console.log(error);
            }
        };
        getHeroe();
    }, [valuesForm]);

    useEffect(() => {
        console.log(heroes);
    }, [heroes]);

    //Formulario de busqueda
    return (
        <>
            <form className="form" onSubmit={formik.handleSubmit}>
                <div className="container">
                    <div className="row">
                        <label htmlFor="searchHero">
                            Buscar Heroe
                        </label>
                        <input
                            className="form-control"
                            type="text"
                            placeholder="Ingrese el nombre del heroe"
                            name="hero"
                            onChange={formik.handleChange}
                            value={formik.values.hero}
                        />
                        <input
                            type="submit"
                            className="btn btn-outline-primary w-25 justify-content-center"
                            value="Buscar"
                        ></input>
                        {formik.errors.hero ? (
                            <div className="alert alert-danger">
                                {formik.errors.hero}
                            </div>
                        ) : null}
                    </div>
                </div>
            </form>
            {/*             {heroes
                ? heroes.map((heroe) => {
                      return (
                          <HeroCard heroe={heroe} key={heroe.id} />
                      );
                  })
                : null} */}
        </>
    );
}

