import React, { useEffect, useState } from 'react';
import axios from 'axios';
import HeroCard from './HeroCard';
import './Home.css';
import LogOut from '../logOut/LogOut';
import '../logOut/LogOut.css';

export default function Home() {
    const [heroId, setHeroId] = useState([]);
    const [heroes, setHeroes] = useState([]);

    //Traer los datos del localStorage y agregarlos a la variable de estado heroes
    useEffect(() => {
        const staff = JSON.parse(localStorage.getItem('staff'));
        if (staff) {
            staff.forEach((hero) => {
                setHeroId((heroes) => [...heroes, hero.id]);
            });
        }
    }, []);

    //Tomar los datos ID de los heroes del equipo y hacer la petición a la API para traer los datos
    useEffect(() => {
        // console.log(heroId);
        const getStaff = () => {
            heroId.forEach(async (id) => {
                const response = await axios.get(
                    `https://www.superheroapi.com/api.php/10221283496530128/${id}`
                );
                setHeroes((heroes) => [...heroes, response.data]);
            });
        };
        getStaff();
    }, [heroId]);

    //Función para evaluar los acumulativos de stats
    useEffect(() => {
        if (heroes.length < heroId.length) return;
        console.log(heroes);
        const totalStats = {};
        let bigger = 0;
        //Tomamos todos los stats y los sumamos
        heroes.forEach((hero) => {
            for (let key in hero.powerstats) {
                if (!totalStats[key]) {
                    totalStats[key] = parseInt(hero.powerstats[key]);
                } else {
                    totalStats[key] += parseInt(hero.powerstats[key]);
                }
            }
        });
        //Evaluamos cual es el mayor y lo guardamos en la variable bigger
        for (let key in totalStats) {
            if (totalStats[key] > bigger) {
                bigger = totalStats[key];
            }
        }

        console.log(totalStats);
        console.log(bigger);
    }, [heroes]);

    return (
        <>
            <div className="box-status-total">
                <h1 className="team-title">SuperHeroes Team</h1>
                <a href="/search" className="link-search-hero">
                    Search
                </a>
                <LogOut />
            </div>
            <div
                className="container"
                style={{
                    marginTop: '2rem'
                }}
            >
                <div className="row">
                    {heroes
                        ? heroes.map((heroe) => {
                              return (
                                  <div className="col-sm-6">
                                      <HeroCard
                                          heroe={heroe}
                                          key={heroe.id}
                                      />
                                  </div>
                              );
                          })
                        : null}
                </div>
            </div>
            <ol
                className="list-group list-group-numbered w-75"
                style={{
                    marginTop: '2rem',
                    marginLeft: 'auto',
                    marginRight: 'auto'
                }}
            >
                <li className="list-group-item list-group-item-dark">
                    A simple dark list group item
                </li>
                <li className="list-group-item">Combat total</li>
                <li className="list-group-item">A list item</li>
                <li className="list-group-item">A list item</li>
                <li className="list-group-item">A list item</li>
                <li className="list-group-item">A list item</li>
            </ol>
        </>
    );
}
