import React, { useEffect, useState } from 'react';
import {
    heroeAlignment,
    heroeAlreadyExist
} from './HeroStaffValidation';

export default function HeroCard(props) {
    // console.log(props.heroe);

    //Estado para seleccionar el heroe
    const [heroe, setHeroe] = useState({});

    let heroImage = props.heroe.image.url,
        heroName = props.heroe.name;

    //Funcion para evaluar si el heroe ya existe en el staff, caso contrario, evaluar la orientación del heroe y comparar con el staff actual y decidir si agregarlo o no
    useEffect(() => {
        if (!heroe.id) return;

        let staff = JSON.parse(localStorage.getItem('staff'));
        // console.log(staff);

        //Evaluamos la existencia del heroe en el staff
        if (heroeAlreadyExist(staff, heroe)) {
            return alert('Este heroe ya pertenece al staff');
        }

        //Evaluamos la orientación del heroe y almacena el resultado en un objeto
        const orientation = heroeAlignment(staff);

        //Funcion para agregar el heroe al staff en caso de positiva evaluación
        const addToStaff = () => {
            let newStaff = [];
            if (staff) {
                newStaff = [...staff, heroe];
            } else {
                newStaff = [heroe];
            }
            localStorage.removeItem('staff');
            localStorage.setItem('staff', JSON.stringify(newStaff));
        };

        if (heroeAlignment().good <= 3 && heroeAlignment().bad <= 3) {
            addToStaff();
        }

        if (heroeAlignment().good === 3) {
            alert(
                'Tu equipo ya dispone de tres heroes con orientación positiva'
            );
        }
        if (heroeAlignment().bad === 3) {
            alert(
                'Tu equipo ya dispone de tres heroes con orientación negativa'
            );
        }
    }, [heroe]);

    //Funcion para seleccionar el heroe y posteriormente verificar su existencia en el staff
    const selectHero = () => {
        setHeroe({
            id: props.heroe.id,
            alignment: props.heroe.biography.alignment
        });
    };

    return (
        <>
            <div className="container">
                <div className="card">
                    <div className="card-body">
                        <img
                            className="card-img-top"
                            src={heroImage}
                            alt={heroName}
                        />
                        <h5 className="card-title text-center">
                            {heroName}
                        </h5>
                        <div className="text-center">
                            <button
                                className="btn btn-secondary"
                                onClick={selectHero}
                            >
                                Agregar al equipo
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
