import React, { useState } from 'react';
import HeroCardShowMoreInfo from './HeroCardShowMoreInfo';
import HeroCardStats from './HeroCardStats';

//funcion para renderizar cada heroe del equipo
export default function CardHeroes(props) {
    //Variable de estado para saber si se muestran datos adicionales o no
    const [showMore, setShowMore] = useState(false);

    let showMoreText = 'Ver Menos';

    //Funcion para mostrar los datos adicionales del heroe
    const showMoreInfo = () => {
        setShowMore(!showMore);
    };

    console.log(props.heroe.appearance);

    let image = props.heroe.image.url,
        name = props.heroe.name;
    return (
        <div
            className="container"
            style={{
                marginTop: '2rem'
            }}
        >
            <div className="card">
                <div className="card-body">
                    <img
                        className="card-img-top"
                        src={image}
                        alt={name}
                    />
                    <h5 className="card-title text-center">{name}</h5>
                    {/* Visualizacion de los stats */}
                    <div
                        className="card-header text-center"
                        style={{
                            fontSize: '1.5rem',
                            fontWeight: 'bold'
                        }}
                    >
                        Status
                    </div>
                    <ul className="list-group list-group-flush">
                        {
                            <HeroCardStats
                                stats={props.heroe.powerstats}
                            />
                        }
                    </ul>

                    <div className="container">
                        <div
                            className="row"
                            style={{
                                justifyContent: 'center'
                            }}
                        >
                            <button
                                className="btn btn-link w-75"
                                onClick={showMoreInfo}
                            >
                                {showMore ? showMoreText : 'Ver M치s'}
                            </button>
                            {/* Visualizar mas informaci칩n */}
                            {showMore && <HeroCardShowMoreInfo />}

                            <button className="btn btn-dark w-75">
                                Eliminar
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
