import React, { useEffect, useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import './Login.css';

export default function Login() {
    //Una vez logueado, si el usuario intenta volver a la pagina de login, se redirecciona a la pagina principal
    useEffect(() => {
        if (localStorage.getItem('token')) {
            alert('Atención ya estás logueado, redirigiendo al home');
            window.location.href = '/';
        }
    }, []);

    const [values, setValues] = useState();

    //Manejo de formulario y errores con formik y yup
    const formik = useFormik({
        initialValues: {
            email: '',
            password: ''
        },
        validationSchema: Yup.object({
            email: Yup.string()
                .email('El email no es válido')
                .required('El campo es requerido'),
            password: Yup.string().required('El campo es requerido')
        }),
        onSubmit: (values) => {
            console.log(values);
            setValues(values);
        }
    });
    // console.log(values);

    //Enviar datos al servidor para validar usuario, si es correcto, se guarda el token en el localStorage y se redirecciona a la pagina principal
    useEffect(() => {
        const getToken = async () => {
            try {
                const response = await axios.post(
                    'http://challenge-react.alkemy.org/',
                    values
                );
                console.log(response);
                if (response.data.token) {
                    localStorage.setItem(
                        'token',
                        response.data.token
                    );
                    //cookie de sesión que expira en cuanto se cierre el navegador para eleminar el token y no poder acceder a la pagina principal sin volver a loguearse
                    document.cookie =
                        'isLoggedIn=' +
                        encodeURIComponent(true) +
                        ';' +
                        'expires=0';
                    window.location.href = '/';
                }
            } catch (error) {
                alert(`Error ${error}`);
            }
        };
        getToken();
    }, [values]);

    return (
        <>
            <h3 className="text-center text-white pt-5">
                Login form
            </h3>
            <div className="container">
                <div className="row justify-content-center align-items-center">
                    <div className="col-md-6">
                        <div id="login-box" className="col-md-12">
                            <form
                                className="form"
                                onSubmit={formik.handleSubmit}
                            >
                                <h3 className="text-center text-info">
                                    Login
                                </h3>
                                <div className="form-group">
                                    <label
                                        htmlFor="username"
                                        className="text-info"
                                    >
                                        Usuario:
                                    </label>
                                    <br />
                                    <input
                                        type="email"
                                        name="email"
                                        placeholder="Ingresa tu email"
                                        onChange={formik.handleChange}
                                        value={formik.values.email}
                                        className="form-control"
                                    />
                                    {formik.errors.email ? (
                                        <div className="form-login__error">
                                            {formik.errors.email}
                                        </div>
                                    ) : null}
                                </div>
                                <div className="form-group">
                                    <label
                                        htmlFor="password"
                                        className="text-info"
                                    >
                                        Contraseña:
                                    </label>
                                    <br />
                                    <input
                                        type="password"
                                        name="password"
                                        placeholder="Ingresa tu contraseña"
                                        onChange={formik.handleChange}
                                        value={formik.values.password}
                                        className="form-control"
                                    />
                                    {formik.errors.password ? (
                                        <div className="form-login__error">
                                            {formik.errors.password}
                                        </div>
                                    ) : null}
                                </div>
                                <div className="form-group">
                                    {/*                                     <label
                                        for="remember-me"
                                        class="text-info"
                                    >
                                        <span>Remember me</span> 
                                        <span>
                                            <input
                                                id="remember-me"
                                                name="remember-me"
                                                type="checkbox"
                                            />
                                        </span>
                                    </label> */}
                                    <br />
                                    <input
                                        type="submit"
                                        name="submit"
                                        className="btn btn-info btn-md"
                                        value="Enviar"
                                    />
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
