import React, { useEffect, useState } from 'react';
import axios from 'axios';
import HeroCardHome from './HeroCardHome';

export default function Home() {
    const [heroId, setHeroId] = useState([]);
    const [heroes, setHeroes] = useState([]);

    //Traer los datos del localStorage y agregarlos a la variable de estado heroes
    useEffect(() => {
        const staff = JSON.parse(localStorage.getItem('staff'));
        if (staff) {
            staff.forEach((hero) => {
                setHeroId((heroes) => [...heroes, hero.id]);
            });
        }
    }, []);

    //Tomar los datos ID de los heroes del equipo y hacer la petición a la API para traer los datos
    useEffect(() => {
        console.log(heroId);
        const getStaff = () => {
            heroId.forEach(async (id) => {
                const response = await axios.get(
                    `https://www.superheroapi.com/api.php/10221283496530128/${id}`
                );
                setHeroes((heroes) => [...heroes, response.data]);
            });
        };
        getStaff();
    }, [heroId]);

    useEffect(() => {
        // console.log(heroes);
    }, [heroes]);

    return (
        <>
            <h1 className="text-center">Equipo</h1>
            <div
                className="container"
                style={{
                    marginTop: '2rem'
                }}
            >
                <div className="row">
                    {heroes
                        ? heroes.map((heroe) => {
                              return (
                                  <div className="col-sm-6">
                                      <HeroCardHome
                                          heroe={heroe}
                                          key={heroe.id}
                                      />
                                  </div>
                              );
                          })
                        : null}
                </div>
            </div>
        </>
    );
}
