//Función para valuar si el heroe ya existe en el staff
export function heroeAlreadyExist(staff, heroe) {
    if (staff) {
        const exist = staff.find((hero) => {
            return hero.id === heroe.id;
        });
        if (exist) return true;
    }
    return false;
}
